import styled from 'styled-components';

export const HeaderSection = styled.header`
  width: 100%;
  max-width: 1440px;
  margin: 0 auto;
  height: 30px;
  background-color: white;
  display: flex;
  align-items: center;
  justify-content: space-between;
  box-shadow: 0px 1px 5px rgb(184, 20, 20);
  padding: 0 20px;
  position: relative;
`;

export const Left = styled.div`
  display: flex;
  align-items: center;

  p {
    color: #000;
    font-weight: 600;
    font-size: 16px;
    line-height: 1;
  }
`;

export const Center = styled.nav`
  display: flex;
  align-items: center;
  justify-content: center;
  flex: 1;

  ul {
    display: flex;
    list-style: none;
    gap: 30px;

    @media (max-width: 768px) {
      display: none;
    }
  }

  ul li {
    position: relative;
    font-size: 14px;
    font-weight: 700;
    cursor: pointer;

    a {
      text-decoration: none;
      color: #000;
    }

    &:after {
      content: "";
      position: absolute;
      background-color: rgb(113, 0, 243);
      height: 2px;
      width: 0%;
      left: 0;
      bottom: -3px;
      transition: 0.3s;
    }

    &:hover:after {
      width: 100%;
    }
  }
`;

export const Right = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
`;

export const Icons = styled.div`
  display: flex;
  align-items: center;
  gap: 15px;

  svg {
    cursor: pointer;
    font-size: 18px;
    color: #000;
    transition: 0.3s;

    &:hover {
      color: rgb(113, 0, 243);
      transform: scale(1.1);
    }
  }

  @media (max-width: 768px) {
    display: none;
  }
`;

export const MenuHamburguer = styled.div`
  display: none;
  position: absolute;
  right: 15px;
  top: 5px;
  z-index: 2;

  div {
    border: solid #272727 0.5px;
    margin: 3px;
    background-color: #FFF;
    height: 3px;
    width: 25px;
  }

  @media (max-width: 768px) {
    display: block;
  }
`;
