import React from 'react';
import * as S from './Styles';
import { Link } from 'react-router-dom'; // se você usar react-router

export default function ProductGallery() {
  // Dados fixos, hardcoded no frontend
  const products = [
    { name: 'Air Force 1', slug: 'air-force-1' },
    { name: 'Dunk', slug: 'dunk' },
    { name: 'Air Jordan 1', slug: 'air-jordan-1' },
    { name: 'Air Max', slug: 'air-max' },
  ];

  return (
    <S.GallerySection>
      <S.GalleryGrid>
        {products.map(({ name, slug }) => (
          <Link
            key={slug}
            to={`/produtos/${slug}`} // link para página do produto
            style={{ textDecoration: 'none' }}
          >
            <S.Card>
              <S.Title>{name}</S.Title>
            </S.Card>
          </Link>
        ))}
      </S.GalleryGrid>
    </S.GallerySection>
  );
}
