import styled from 'styled-components';




export const HeaderSection = styled.section` //arrumar dps
  height: 40px;
  background-color:#fff;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Left = styled.section`
  display: flex;
  align-items: center;
  flex-grow: 1;

  p {
    margin-left: 25px;
    color: #000; 
    font-weight: 600;
    font-size: 20px;
    height: 14px;
  }
`;

export const Right = styled.section`
  display: flex;
  align-items: center;
  flex-grow: 1;
`;




export const Navegation = styled.nav`
  ul {
    display: flex;
    list-style: none;
    position: relative;

    @media (max-width: 768px) {
      display: none;
    }
  }

  ul li {
    cursor: pointer;
    margin-left: 19px;
    font-size: 17px;
    font-weight: 750;
    position: relative;

    &:after {
      content: "";
      position: absolute;
      background-color: rgb(113, 0, 243);
      height: 3px;
      width: 0%;
      left: 0;
      bottom: -3px;
      transition: 0.5s;
    }

    &:hover:after {
      width: 100%;
    }

    .dropdown {
      display: none;
      position: absolute;
      top: 25px;
      left: 0px;
      background-color: white;
      border-radius: 6px;
      box-shadow: 0 2px 8px rgba(0,0,0,0.2);
      padding: 10px 100;
      z-index: 5;
      min-width: 150px;

      a {
        display: block;
        padding: 10px 100px;
        color: black;
        text-decoration: none;
        white-space: nowrap;

        &:hover {
          background-color: rgb(238, 238, 238);
        }
      }
    }

    &:hover .dropdown {
      display: block;
    }
  }

  a {
    text-decoration: none;
    color: #000;
  }
`;

export const MenuHamburguer = styled.div`
  position: absolute;
  right: 5px;
  top: 5px;
  display: none;
  z-index: 2;

  div {
    border: solid #272727 0.5px;
    margin: 4px;
    background-color: #FFF;
    height: 4px;
    width: 40px;
  }

  @media (max-width: 768px) {
    display: block;
  }
`;
export const Icons = styled.div`
  display: flex;
  align-items: center;
  gap: 20px;
  margin-right: 30px;

  svg {
    cursor: pointer;
    font-size: 23px;
    color: #000;
    transition: 0.3s;

    &:hover {
      color: rgb(113, 0, 243);
      transform: scale(1.1);
    }
  }

  @media (max-width: 768px) {
    display: none;
  }
`;
